package com.vika.way.pre.algorithm.leetcode.midium.S401_500;

public class S468ValidateIPAddress {

    public static void main(String[] args) {
        S468ValidateIPAddress solution = new S468ValidateIPAddress();
        System.out.println(solution.validIPAddress("..."));
    }

    public String validIPAddress(String IP) {
        if (IP.contains(".")) {
            String[] strs = IP.split("\\.", -1);
            if (strs.length != 4) {
                return result(0);
            }
            for (int i = 0; i < strs.length; i++) {
                if (strs[i].length() == 0) {
                    return result(0);
                }
                int num = 0;
                for (int j = 0; j < strs[i].length(); j++) {
                    char c = strs[i].charAt(j);
                    if (j == 0 && c == '0' && j < strs[i].length() - 1) {
                        return result(0);
                    }
                    if (c < '0' || c > '9') {
                        return result(0);
                    }
                    num = num * 10 + (c - '0');
                    if (num > 255) {
                        return result(0);
                    }
                }
            }
            return result(1);
        } else if (IP.contains(":")) {
            String[] strs = IP.toUpperCase().split(":", -1);
            if (strs.length != 8) {
                return result(0);
            }
            for (int i = 0; i < strs.length; i++) {
                if (strs[i].length() == 0 || strs[i].length() > 4) {
                    return result(0);
                }
                for (int j = 0; j < strs[i].length(); j++) {
                    char c = strs[i].charAt(j);
                    if ((c < '0' || c > '9') && (c < 'A' || c > 'F')) {
                        return result(0);
                    }
                }
            }
            return result(2);
        } else {
            return result(0);
        }
    }

    public String result(int r) {
        if (r == 1) {
            return "IPv4";
        } else if (r == 2) {
            return "IPv6";
        } else {
            return "Neither";
        }
    }
}
